INSERT INTO `Abilities` VALUES
    (1, "Attack (BattleAxe)", "1d8/1d10 Slashing Damage", NULL, "action"),
    (2, "Drink Health Potion", "Heal 2d4", NULL, "action"),
    (3, "Attack (Longbow)", "1d8 Piercing", NULL, "action"),
    (4, "Hybrid Transformation", "As a bonus action, you transform into a special hybrid form for up to 1 hour. You can speak, use equipment, and wear armor while in this form, and can revert to your normal form as a bonus action. You automatically revert to your normal form if you fall unconscious or die.", 8, "bonus-action"),
    (5, "Rage", "In battle, you fight with primal ferocity. On your turn, you can enter a rage as a bonus action.\r\nWhile raging, you gain the following benefits if you aren\'t wearing heavy armor:\r\nYou have advantage on Strength checks and Strength saving throws.\r\nWhen you make a melee weapon attack using Strength, you gain a bonus to the damage roll that increases as you gain levels as a barbarian, as shown in the Rage Damage column of the Barbarian table.\r\nYou have resistance to bludgeoning, piercing, and slashing damage.\r\nIf you are able to cast spells, you can\'t cast them or concentrate on them while raging.\r\nYour rage lasts for 1 minute. It ends early if you are knocked unconscious or if your turn ends and you haven\'t attacked a hostile creature since your last turn or taken damage since then. You can also end your rage on your turn as a bonus action.\r\nOnce you have raged the number of times shown for your barbarian level in the Rages column of the Barbarian table, you must finish a long rest before you can rage again", NULL, "bonus-action"),
    (6, "Unarmed Strike", "Strike with your fists.", NULL, "action"),
    (7, "Dash", "Move up to double your movement for this turn.", NULL, "action"),
    (8, "Dash (Bonus Action)", "Move up to double your movement for this turn.", NULL, "bonus-action"),
    (9, "Attack (Dagger)", "1d4 Slashing", NULL, "action");
